mapper.update:
update
ACT_RU_TASK
set
SUSPENSION_STATE_ = #{status}
where ID_ = #{id}

update
ACT_RU_TASK
set
SUSPENSION_STATE_ = #{status}
where PROC_DEF_ID_ = #{id}

mapper.select:
select p.ID_ AS "id", p.CATEGORY_ AS "category", p.NAME_ AS "name", p.KEY_ AS "key", p.VERSION_ AS "version", p.DEPLOYMENT_ID_ AS "deploymentId", p.RESOURCE_NAME_ AS "resourceName", p.DGRM_RESOURCE_NAME_ AS "dgrmResourceName", DESCRIPTION_ AS "description", p.SUSPENSION_STATE_ AS "suspensionState", d.DEPLOY_TIME_ as deployTime from act_re_procdef p left join ACT_RE_DEPLOYMENT d on p.DEPLOYMENT_ID_ = d.ID_ where 1=1
and ( p.NAME_ LIKE CONCAT(CONCAT('%', #{paramCondition.keywords}),'%') )
and d.DEPLOY_TIME_ >= #{paramCondition.lastStart}
and d.DEPLOY_TIME_ <= #{paramCondition.lastEnd}
order by d.DEPLOY_TIME_ desc

select ht.*, hv.TEXT_ from ACT_HI_ACTINST ht left join ACT_HI_VARINST hv on ht.TASK_ID_ = hv.TASK_ID_ where 1=1
and ht.PROC_INST_ID_ = #{procInstId}
order by ht.ID_
